CREATE TABLE IF NOT EXISTS %PREFIX%_saml_clients ( smc_id integer unsigned AUTO_INCREMENT, smc_uuid varchar(36) NOT NULL, smc_org_id integer unsigned NOT NULL, smc_client_id varchar(255) NOT NULL UNIQUE, smc_client_name varchar(255) NOT NULL, smc_enabled bool DEFAULT true, smc_metadata_url text NULL, smc_acs_url text NOT NULL, smc_slo_url text NULL, smc_x509_certificate text NOT NULL, smc_userid_field varchar(50) NOT NULL default 'usr_id', smc_field_mapping text NULL, smc_role_mapping text NULL,
