smc_usr_id_create integer unsigned, smc_timestamp_create timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, smc_usr_id_change integer unsigned, smc_timestamp_change timestamp NULL DEFAULT NULL, PRIMARY KEY (smc_id) ) ENGINE = InnoDB DEFAULT character SET = utf8 COLLATE = utf8_unicode_ci;

